mapper.insert:
INSERT INTO free_board(bo_no
					, bo_title
					, bo_category
					, bo_writer
					, bo_pass
					, bo_content
					, bo_mod_date)
VALUES(seq_freeboard.nextval,#{boTitle}
						   ,#{boCategory}
						   ,#{boWriter}
						   ,#{boPass}
						   ,#{boContent}
						   ,SYSDATE)

mapper.update:
UPDATE free_board
SET bo_hit = bo_hit+1
WHERE bo_no = #{boNo}
